mapper.select:
SELECT 

FROM affair_commentators a

WHERE a.id = #{id}

SELECT
FROM affair_commentators a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%') '%'||#{name}||'%'
AND a.unit LIKE '%'||#{unit}||'%' '%'+#{unit}+'%' concat('%',#{unit},'%') '%'||#{unit}||'%'
AND a.duty LIKE '%'||#{duty}||'%' '%'+#{duty}+'%' concat('%',#{duty},'%') '%'||#{duty}||'%'
AND a.account LIKE '%'||#{account}||'%' '%'+#{account}+'%' concat('%',#{account},'%') '%'||#{account}||'%'
/*${sqlMap.dsf}*/
AND a.unit_id = #{unitId}
OR a.create_by = #{userId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM affair_commentators a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

