mapper.select:
SELECT

FROM affair_party_member a

WHERE a.id = #{id}

		SELECT

		FROM affair_party_member a

		LEFT JOIN affair_general_situation s ON s.id = a.party_branch_id

			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.name LIKE concat('%',#{name},'%')


				AND a.card_num LIKE concat('%',#{cardNum},'%')


				AND a.sex = #{sex}


				AND (a.party_branch_id = #{partyBranchId} OR s.parent_ids LIKE CONCAT('%,', #{partyBranchId}, ',%'))


				AND a.personnel_category = #{personnelCategory}



                    AND a.join_date between #{joinStartDate} and #{joinEndDate}


					AND a.join_date >= #{joinStartDate}


					AND  #{joinEndDate} >= a.join_date







                    AND a.turn_date between #{turnStartDate} and #{turnEndDate}


					AND a.turn_date >= #{turnStartDate}


					AND  #{turnEndDate} >= a.turn_date









				ORDER BY ${page.orderBy}



				ORDER BY

				cast(nullif(split_part(a.sort, '-', 1),'') as integer),
				cast(nullif(split_part(a.sort, '-', 2),'')as integer) ,
				cast(nullif(split_part(a.sort, '-', 3),'')as integer) ,
				cast(nullif(split_part(a.sort, '-', 4),'')as integer)

SELECT

FROM affair_party_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

