SELECT
apm.party_branch "label",
count( apm.party_branch )
FROM
affair_party_member apm
WHERE apm.del_flag = '0'

 AND apm.party_branch_id = #{id}



		AND TO_CHAR(apm.turn_date, 'yyyy-mm') = #{month}


		AND extract(year from apm.turn_date) = #{year}


		AND apm.turn_date between  #{startDate,jdbcType=DATE} and #{endDate,jdbcType=DATE}


		AND apm.turn_date >= #{startDate,jdbcType=DATE}


		AND  #{endDate,jdbcType=DATE} >= apm.turn_date




GROUP BY
apm.party_branch

