mapper.select:
select count(1) as num ,'全局党员数' as label
from affair_party_member a
where a.del_flag = '0'


		AND TO_CHAR(a.join_date, 'yyyy-mm')   <= #{month}


		AND extract(year from a.join_date) <=  #{year}


		AND a.join_date between  #{startDate,jdbcType=DATE} and #{endDate,jdbcType=DATE}


		AND a.join_date >= #{startDate,jdbcType=DATE}


		AND  #{endDate,jdbcType=DATE} >= a.join_date




union all
