mapper.select:
SELECT 

FROM affair_security_check a

WHERE a.id = #{id}

SELECT 

FROM affair_security_check a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.date BETWEEN #{beginDate} AND #{endDate}


			AND a.date >= #{beginDate}


			AND a.date <= #{endDate}



		AND a.content LIKE '%'||#{content}||'%'


		AND a.situation LIKE '%'||#{situation}||'%'


		AND a.measure LIKE '%'||#{measure}||'%'

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM affair_security_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE affair_security_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE affair_security_check SET
del_flag = '1'
WHERE id in #{item}
