mapper.select:
SELECT 

FROM affair_training_manage a

WHERE a.id = #{id}

SELECT
FROM affair_training_manage a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%') '%'||#{title}||'%'
AND a.type LIKE '%'||#{type}||'%' '%'+#{type}+'%' concat('%',#{type},'%') '%'||#{type}||'%'
AND a.level LIKE '%'||#{level}||'%' '%'+#{level}+'%' concat('%',#{level},'%') '%'||#{level}||'%'
AND a.unit LIKE '%'||#{unit}||'%' '%'+#{unit}+'%' concat('%',#{unit},'%') '%'||#{unit}||'%'
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%') '%'||#{title}||'%'
AND a.begin_time between #{beginTime} and #{endTime}
AND a.begin_time >= #{beginTime}
AND #{endTime} >= a.end_time
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM affair_training_manage a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE affair_training_manage SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE affair_training_manage SET
del_flag = '1'
WHERE id in #{item}
