mapper.select:
SELECT 

FROM personnel_org_check a

WHERE a.id = #{id}

SELECT 

FROM personnel_org_check a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}



			AND a.date between #{startDate} and #{endDate}


			AND a.date >= #{startDate}


			AND  #{endDate} >= a.date





		AND a.department LIKE '%'||#{department}||'%'


		AND a.name LIKE '%'||#{name}||'%'


		AND a.id_number = #{idNumber}




		ORDER BY ${page.orderBy}


		ORDER BY a.date ASC

SELECT 

FROM personnel_org_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE personnel_org_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE personnel_org_check SET
del_flag = '1'
WHERE id in #{item}

UPDATE personnel_org_check SET
del_flag = '1'
WHERE id_number in #{idNumber}
