mapper.select:
SELECT 

FROM personnel_party_member a

WHERE a.id = #{id}

SELECT 

FROM personnel_party_member a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.pass_date between #{startDate} and #{endDate}


			AND a.pass_date >= #{startDate}


			AND  #{endDate} >= a.pass_date





		AND a.id_number = #{idNumber}




		ORDER BY ${page.orderBy}


		ORDER BY a.pass_date asc

SELECT 

FROM personnel_party_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE personnel_party_member SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE personnel_party_member SET
del_flag = '1'
WHERE id in #{item}

UPDATE personnel_party_member SET
del_flag = '1'
WHERE id_number in #{idNumber}
